portsIn.value:
width:100px;
height:100px;
left:50%;
top:50%;
border-radius:100%;

margin-left:-50px;
position:absolute;
cursor:pointer;
opacity:0.7;
transform:translate(0,-50%);
z-index:999999;
background-color:#333;
border:5px solid #333;

border-style:solid;
border-color:transparent transparent transparent #ccc;
box-sizing:border-box;
width:50px;
height:50px;
margin-top:25px;
margin-left:36px;
border-width:25px 0px 25px 40px;
pointer-events:none;

#define PI 3.1415926538

UNI sampler2D tex;
UNI float t;
UNI float c;
UNI float w;
UNI float h;
UNI float s;

// tex will be set by shaderEffect op with the current content of the image compose

IN vec2 texCoord;
{{MODULES_HEAD}}
void main()
{   
    float wRoad = 0.07;
    float angleCenter = texCoord.y * PI * 3.0;
    angleCenter += t * s;

    float centerOff = (
        sin(angleCenter * 0.25) +
		sin(angleCenter) + 
		sin(angleCenter*0.5) 
	) * wRoad;

    vec4 col = texture2D(tex, texCoord);
    col = col * pow( abs(cos( (texCoord.x + c - centerOff) * PI )), w ) * h;



    {{MODULE_COLOR}}
    outColor = col;
}

{{MODULES_HEAD}}
IN vec3 vPosition;
IN vec2 attrTexCoord;
IN vec3 attrVertNormal;
IN float attrVertIndex;
OUT vec2 texCoord;
OUT vec3 norm;
UNI mat4 projMatrix;
UNI mat4 viewMatrix;
UNI mat4 modelMatrix;
void main()
{
   texCoord=attrTexCoord;
   norm=attrVertNormal;
   vec4 pos=vec4(vPosition,  1.0);
   mat4 mMatrix=modelMatrix;
   {{MODULE_VERTEX_POSITION}}
   gl_Position = projMatrix * (viewMatrix*mMatrix) * pos;
}
